import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './TripInfo.css';

function TripInfo() {
    const { id } = useParams();
    const [trip, setTrip] = useState(null);
    const [error, setError] = useState(null);
    const [nights, setNights] = useState(0); // State for nights
    const [endDate, setEndDate] = useState(''); // State for dynamically updated end_date
    const [accommodation, setAccommodation] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [newAccommodation, setNewAccommodation] = useState({
        name: '',
        price: '',
        address: '',
        city: '',
        country: '',
    });

    const formatDate = (dateString) => {
        const options = { year: 'numeric', month: 'long', day: 'numeric' };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    const calculateNights = (startDate, endDate) => {
        const start = new Date(startDate);
        const end = new Date(endDate);
        const difference = Math.ceil((end - start) / (1000 * 60 * 60 * 24));
        return difference;
    };

    const calculateEndDate = (startDate, nights) => {
        const start = new Date(startDate);
        const updatedEndDate = new Date(start);
        updatedEndDate.setDate(start.getDate() + nights); // Add the nights to the start date
        return updatedEndDate.toISOString();
    };

    useEffect(() => {
        const fetchTripDetails = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('No token found');
                return;
            }

            try {
                const response = await axios.get(`http://localhost:5000/api/trips/${id}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                console.log('Trip details:', response.data);
                setTrip(response.data);
                setAccommodation(response.data.accommodation || []);

                // Calculate initial nights and set the endDate
                const initialNights = calculateNights(response.data.start_date, response.data.end_date);
                setNights(initialNights);
                setEndDate(response.data.end_date);
            } catch (err) {
                console.error('Error fetching trip details:', err);
                setError('Failed to fetch trip details.');
            }
        };

        fetchTripDetails();
    }, [id]);

    const handleIncrement = () => {
        const updatedNights = nights + 1;
        setNights(updatedNights);
        setEndDate(calculateEndDate(trip.start_date, updatedNights));
    };

    const handleDecrement = () => {
        if (nights > 0) {
            const updatedNights = nights - 1;
            setNights(updatedNights);
            setEndDate(calculateEndDate(trip.start_date, updatedNights));
        }
    };

    const handleAddAccommodation = async () => {
        const token = localStorage.getItem('token');
        try {
            const response = await axios.post(
                `http://localhost:5000/api/trips/${id}/accommodation`,
                newAccommodation,
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );
            setAccommodation([...accommodation, response.data]);
            setShowForm(false);
            setNewAccommodation({ name: '', price: '', address: '', city: '', country: '' });
        } catch (err) {
            console.error(err);
            alert('Failed to add accommodation.');
        }
    };

    if (error) return <p style={{ color: 'red' }}>{error}</p>;
    if (!trip) return <p>Loading...</p>;

    return (
        <div className="trip-info">
            <div className="trip-header">
                <h1>{trip.trip_name}</h1>
                <p>
                    Selected Dates: <strong>{formatDate(trip.start_date)}</strong> - <strong>{formatDate(endDate)}</strong>
                </p>
            </div>
            <div className="trip-body">
                <div className="trip-summary">
                    <h2>Plan</h2>
                    <div className="summary-grid">
                        <div>
                            <strong>Country:</strong> {trip.selected_country || 'Unknown'}
                        </div>
                        <div className="nights-counter">
                            <strong>Nights:</strong>
                            <div className="counter">
                                <button onClick={handleDecrement}>-</button>
                                <span>{nights}</span>
                                <button onClick={handleIncrement}>+</button>
                            </div>
                        </div>
                        <div>
                            <strong>Sleeping:</strong> {trip.sleeping || 'N/A'}
                        </div>
                        <div>
                            <strong>Discover:</strong> {trip.discover || 'N/A'}
                        </div>
                        <div>
                            <strong>Transport:</strong> {trip.transport || 'N/A'}
                        </div>
                    </div>
                </div>

                <h2>Accommodations</h2>
                {accommodation.length > 0 ? (
                    accommodation.map((item, index) => (
                        <div key={index} className="accommodation-item">
                            <h3>{item.name}</h3>
                            <p>Price: ${item.price}</p>
                            <p>Address: {item.address}</p>
                            <p>City: {item.city}</p>
                            <p>Country: {item.country}</p>
                        </div>
                    ))
                ) : (
                    <p>No accommodations added yet.</p>
                )}

                <button onClick={() => setShowForm(!showForm)} className="add-destination-button">
                    {showForm ? 'Cancel' : '+'}
                </button>

                {showForm && (
                    <form onSubmit={(e) => e.preventDefault()} className="accommodation-form">
                        <input
                            type="text"
                            placeholder="Name"
                            value={newAccommodation.name}
                            onChange={(e) => setNewAccommodation({ ...newAccommodation, name: e.target.value })}
                        />
                        <input
                            type="number"
                            placeholder="Price"
                            value={newAccommodation.price}
                            onChange={(e) => setNewAccommodation({ ...newAccommodation, price: e.target.value })}
                        />
                        <input
                            type="text"
                            placeholder="Address"
                            value={newAccommodation.address}
                            onChange={(e) => setNewAccommodation({ ...newAccommodation, address: e.target.value })}
                        />
                        <input
                            type="text"
                            placeholder="City"
                            value={newAccommodation.city}
                            onChange={(e) => setNewAccommodation({ ...newAccommodation, city: e.target.value })}
                        />
                        <input
                            type="text"
                            placeholder="Country"
                            value={newAccommodation.country}
                            onChange={(e) => setNewAccommodation({ ...newAccommodation, country: e.target.value })}
                        />
                        <button onClick={handleAddAccommodation}>Save</button>
                    </form>
                )}
            </div>
        </div>
    );
}

export default TripInfo;
