import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './Destination.css';

function Destination() {
    const { id } = useParams();
    const [selectedCountry, setSelectedCountry] = useState('');
    const navigate = useNavigate();

    const handleDestinationSelection = (country) => {
        setSelectedCountry(country);
        // Navigate to trip info with the selected destination
        navigate(`/trip-info/${id}`, { state: { selectedCountry: country } });
    };

    return (
        <div className="destination-container">
            {!selectedCountry ? (
                <div className="destination-input-container">
                    <h2>Your trip to</h2>
                    <input
                        type="text"
                        placeholder="Search any place in the world..."
                        onKeyDown={(e) => {
                            if (e.key === 'Enter' && e.target.value.trim()) {
                                handleDestinationSelection(e.target.value.trim());
                            }
                        }}
                    />
                </div>
            ) : (
                <p>Loading...</p> // Optional loading state before navigating
            )}
        </div>
    );
}

export default Destination;
