import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './Destination.css';
import axios from 'axios';

function Destination() {
    const { id } = useParams();
    const [trip, setTrip] = useState(null);
    const [selectedCountry, setSelectedCountry] = useState(''); // For displaying the selected country
    const [searchInput, setSearchInput] = useState(''); // For the search bar input
    const navigate = useNavigate();

    // Fetch trip details
    useEffect(() => {
        const fetchTrip = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('No token found');
                return;
            }

            try {
                const response = await axios.get(`http://localhost:5000/api/trips/${id}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setTrip(response.data);
                setSelectedCountry(response.data.selected_country || ''); // Initialize the displayed country
            } catch (error) {
                console.error('Error fetching trip:', error);
            }
        };

        fetchTrip();
    }, [id]);

    // Handle destination selection
    const handleDestinationSelection = async () => {
        if (!searchInput.trim()) return;

        try {
            const token = localStorage.getItem('token');
            await axios.put(
                `http://localhost:5000/api/trips/${id}`,
                { selected_country: searchInput }, // Update the destination in the backend
                { headers: { Authorization: `Bearer ${token}` } }
            );
            setSelectedCountry(searchInput); // Update the displayed country after successful API call
            navigate(`/trip-info/${id}`);
        } catch (error) {
            console.error('Error updating destination:', error);
        }
    };

    if (!trip) return <p>Loading...</p>;

    return (
        <div className="destination-container">
            <h1>Your trip to {selectedCountry || 'Select a country'}</h1>
            <div className="destination-input-container">
                <input
                    type="text"
                    className="destination-input"
                    placeholder="Search any place in the world..."
                    value={searchInput}
                    onChange={(e) => setSearchInput(e.target.value)} // Update search input state
                    onKeyDown={(e) => {
                        if (e.key === 'Enter') {
                            handleDestinationSelection();
                            console.log('Updating trip ID:', id);

                        }
                    }}
                />
            </div>
        </div>
    );
}

export default Destination;
