import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './Destination.css';
import axios from 'axios';

function Destination() {
    const { id } = useParams();
    const [trip, setTrip] = useState(null);
    const [selectedCountry, setSelectedCountry] = useState('');
    const [typedDestination, setTypedDestination] = useState('');
    const [destinations, setDestinations] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchTrip = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('No token found');
                return;
            }

            try {
                const response = await axios.get(`http://localhost:5000/api/trips/${id}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setTrip(response.data);
                setSelectedCountry(response.data.selected_country || '');
                setDestinations(response.data.destinations || []);
            } catch (error) {
                console.error('Error fetching trip:', error);
            }
        };

        fetchTrip();
    }, [id]);

    const handleInputChange = (e) => {
        const value = e.target.value;
        setTypedDestination(value);

        // Prepend the typed value to destinations immediately
        if (value.trim()) {
            const updatedDestinations = [{ name: value }, ...destinations];
            setDestinations(updatedDestinations);
        }
    };

    const handleSubmit = async () => {
        if (!typedDestination.trim()) return;

        try {
            const token = localStorage.getItem('token');
            const updatedDestinations = [{ name: typedDestination }, ...destinations];
            await axios.put(
                `http://localhost:5000/api/trips/${id}/destinations`,
                { destinations: updatedDestinations },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            setDestinations(updatedDestinations);
            setTypedDestination('');
        } catch (error) {
            console.error('Error updating destinations:', error);
        }
    };

    return (
        <div className="destination-container">
            <h1>Your trip to {selectedCountry || 'Select a country'}</h1>
            <div className="destination-input-container">
                <input
                    type="text"
                    className="destination-input"
                    placeholder="Search any place in the world..."
                    value={typedDestination}
                    onChange={handleInputChange}
                    onKeyDown={(e) => {
                        if (e.key === 'Enter') {
                            handleSubmit();
                        }
                    }}
                />
            </div>
            <div>
                <h2>Destinations</h2>
                {destinations.map((dest, index) => (
                    <div key={index} className="destination-item">
                        <span>{`${index + 1}. ${dest.name}`}</span>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Destination;
